INSERT INTO `m_music` VALUES ('367', '一个人一座城', '乔任梁', 'http://singerimg.kugou.com/uploadpic/softhead/400/20160918/20160918181439971.jpg', '38B04DE984029314DD0007FDF4919E26', '我愿用十年，换你一句好久不见', '[00:00.46]一个人一座城 - 乔任梁\r\n[00:00.82]词：胡小健\r\n[00:01.28]曲：顾雄\r\n[00:05.98]头顶的叶子 把阳光挡起\r\n[00:13.08]投下的影子 摇晃记忆\r\n[00:20.35]想像着一次 若能再相遇\r\n[00:27.73]你会不会 忘记了过去\r\n[00:35.00]在这阳光 肆虐的城市里\r\n[00:42.27]潜藏着 另一个自己\r\n[00:49.48]谈天说地聊着 都好风趣\r\n[00:56.89]关上了门 自己怕自己\r\n[01:04.52]我 要找一个人 会多残忍\r\n[01:11.88]不完整的 交换不完整\r\n[01:19.40]我 在这城市里 等了又等\r\n[01:26.89]等待着下一次 可能\r\n[01:49.64]头顶的叶子 把阳光挡起\r\n[01:56.68]投下的影子 摇晃记忆\r\n[02:04.13]想像着此刻 若能再相遇\r\n[02:11.65]你会不会 忘记了过去\r\n[02:19.22]我 要找一个人 会多残忍\r\n[02:26.44]不完整的 交换不完整\r\n[02:33.77]我 在这城市里 等了又等\r\n[02:41.43]等待着下一次 可能\r\n[02:48.91]等待着下一次 可能\r\n');
