INSERT INTO `m_music` VALUES ('393', '假装我没有爱过', '威仔', 'http://singerimg.kugou.com/uploadpic/softhead/400/20180919/20180919194653213.jpg', 'F17359F95A08008EC4A3EE7FDA535E15', '每周推荐歌曲', '[00:00.00]威仔 - 假装我没有爱过\r\n[00:00.72]作词：威仔\r\n[00:00.92]作曲：威仔\r\n[00:01.08]编曲：微笑\r\n[00:01.23]混音：贾佳\r\n[00:01.38]OP：云猫文化 In Move\r\n[00:01.67]制作人：威仔\r\n[00:23.09]合：\r\n[00:23.98]就当你没有来过\r\n[00:26.82]假装我没有爱过\r\n[00:29.71]告诉自己别难过\r\n[00:32.50]都是你配不上我\r\n[00:35.40]若你从没心动过\r\n[00:38.32]当初何必说爱我\r\n[00:41.11]最怕深情付出后\r\n[00:43.99]还是一个人生活\r\n[00:46.77]威仔：\r\n[00:47.54]秋天的落叶\r\n[00:48.69]在我面前掉落\r\n[00:50.11]现在的你是否记得\r\n[00:51.48]曾经说过爱我\r\n[00:52.88]最后的你\r\n[00:53.94]真的没有再回头\r\n[00:55.77]我还在怀念你\r\n[00:56.78]你却已经忘了我\r\n[00:58.61]曾经付出那么多\r\n[01:00.03]还是没有结果\r\n[01:01.40]或许你的心里\r\n[01:02.48]我从来没存在过\r\n[01:04.30]你只是随便说说\r\n[01:05.72]从来没有行动\r\n[01:07.14]而我信了你的承诺\r\n[01:08.61]变得要死要活\r\n[01:10.08]曾经以为牵了手后\r\n[01:11.54]就能够永久\r\n[01:12.85]最后你给我的\r\n[01:13.92]也全部都是失落\r\n[01:15.70]我苦苦的哀求\r\n[01:17.12]你如此冷漠\r\n[01:18.59]我觉得是错过\r\n[01:20.03]你却说是解脱\r\n[01:21.47]好吧或许走到今天\r\n[01:22.94]都是我一人的错\r\n[01:24.30]也许到最后你也从来没有\r\n[01:26.26]喜欢过我\r\n[01:27.14]牵过你的手\r\n[01:28.56]吻过你额头\r\n[01:29.99]就这样吧\r\n[01:31.22]曾经拥有我已足够\r\n[01:32.45]阿夏：\r\n[01:32.85]就当你没有来过\r\n[01:35.41]假装我没有爱过\r\n[01:38.26]告诉自己别难过\r\n[01:41.11]都是你配不上我\r\n[01:43.96]若你从没心动过\r\n[01:46.80]当初何必说爱我\r\n[01:49.68]最怕深情付出后\r\n[01:52.57]还是一个人生活\r\n[01:56.14]威仔：\r\n[01:57.57]你听\r\n[01:59.40]外面又下雨了\r\n[02:02.14]我记得\r\n[02:03.92]你最爱听下雨的声音\r\n[02:18.23]为什么失去了爱情\r\n[02:20.05]我才懂得珍惜\r\n[02:21.48]不知道以后还能不能\r\n[02:23.05]再见到你\r\n[02:24.23]你发的语音\r\n[02:25.73]调到最大声音\r\n[02:27.10]总在夜里想你的时候\r\n[02:28.72]我一遍一遍听\r\n[02:30.02]或许真的是我太过爱你\r\n[02:31.90]才会猜疑\r\n[02:32.88]导致最后的你\r\n[02:33.99]消失在了人海里\r\n[02:35.74]每一个微笑\r\n[02:37.15]还是如此清晰\r\n[02:38.69]却不再奢求能够和你在一起\r\n[02:41.13]合：\r\n[02:41.22]就当你没有来过\r\n[02:43.95]假装我没有爱过\r\n[02:46.79]告诉自己别难过\r\n[02:49.68]都是你配不上我\r\n[02:52.51]若你从没心动过\r\n[02:55.38]当初何必说爱我\r\n[02:58.22]最怕深情付出后\r\n[03:01.11]还是一个人生活\r\n[03:04.89]就当你没来过\r\n[03:09.67]告诉自己别难过\r\n[03:16.24]最怕深情付出以后\r\n[03:21.21]还是一个人生活\r\n');
