INSERT INTO `m_music` VALUES ('571', '红线', '残音', 'http://singerimg.kugou.com/uploadpic/softhead/400/20180901/20180901170952299.jpg', 'C47851276499BF787774C20F1748CDD3', '小编每日精选：原创古风', '[00:00.36]残音、阿南sara - 红线\r\n[00:01.91]作词：小宝君\r\n[00:03.13]作曲：亦是驼羊、向往\r\n[00:20.91]青丝轻履檐上登\r\n[00:23.29]紫绣短袍掠疾风\r\n[00:25.68]龙纹照月刃覆霜雪\r\n[00:27.72]长发挽钗上有神名\r\n[00:30.45]百里往还只三更\r\n[00:32.61]一叶堕露草吟风\r\n[00:35.12]下越春雷飞掠千峰\r\n[00:37.35]踏云而归未惊鸡鸣\r\n[00:40.37]听帐外戈甲鸣动\r\n[00:44.88]七星剑刃上血痕犹殷红\r\n[00:49.71]拿得金盒不劳擒纵\r\n[00:54.35]残灯烬燃长空\r\n[00:58.26]追忆前世曾施百草救众生\r\n[01:02.74]错斟芫酒转世功过自分明\r\n[01:07.62]此生布衣为人臣妾\r\n[01:10.92]亦能弭兵祸\r\n[01:13.00]免却天下战火只一夜盗盒\r\n[01:38.16]十二品阮咸声动\r\n[01:40.02]七情收尽宫商中\r\n[01:42.31]经史万卷笔下生风\r\n[01:44.45]挥毫如剑须眉皆惊\r\n[01:47.24]潜世间不露峥嵘\r\n[01:49.35]战祸起脱颖成锋\r\n[01:51.88]烛火未灭中天月过\r\n[01:54.11]铜台之上掠似飞鸿\r\n[01:57.16]听帐外传叫风生\r\n[02:01.46]移形影帐侧荒草未曾动\r\n[02:06.81]拿得金盒不劳擒纵\r\n[02:11.23]月下奔走无踪\r\n[02:14.90]追忆平生为君记室府邸中\r\n[02:19.42]随侍如影大隐不羡罗绮荣\r\n[02:24.41]千里盗盒不爱金帛\r\n[02:27.71]以报君恩德\r\n[02:29.80]免却天下战火愿生死间过\r\n[02:54.83]夜开宴浩歌以送\r\n[02:59.40]瞰长川星河影间白浪涌\r\n[03:04.73]身没长夜不见影踪\r\n[03:08.74]唯剩流水对风\r\n[03:12.35]龙纹短刃五寸减处三年功\r\n[03:17.03]拂衣藏踪林泉相和歌从容\r\n[03:21.89]盗得金盒乱臣知惧\r\n[03:25.46]良将敛兵戈\r\n[03:27.49]免却天下战火留此红线歌\r\n');
