INSERT INTO `m_music` VALUES ('605', '青衣望月', '童珺', 'http://singerimg.kugou.com/uploadpic/softhead/400/20180530/20180530173919115.jpg', '7E392F22E606F5061E02C9F253C0D813', '小编每日精选：原创古风', '[00:00.27]童珺 - 青衣望月\r\n[00:01.34]作词：姚雨彤\r\n[00:02.20]作曲：梁成\r\n[00:02.96]Rap：十二\r\n[00:17.63]烟波里 草木清\r\n[00:21.87]你送我的青衣\r\n[00:24.94]桃花林 良人吟 月晓风清\r\n[00:32.34]夜微凉 星空冷\r\n[00:36.70]花落谁看晨曦\r\n[00:40.39]这痴人曲 梦一世终会醒\r\n[00:46.98]月光下远去的背影\r\n[00:50.52]断琴弦心碎在手里\r\n[00:55.38]褪色的青衣\r\n[00:58.16]我依然还在等你\r\n[01:01.64]月光下远去的背影\r\n[01:05.33]繁星泪流干在心里\r\n[01:10.09]泼墨的痕迹\r\n[01:11.70]是今生的约定\r\n[01:31.47]烟波里 草木清\r\n[01:35.77]你送我的青衣\r\n[01:38.75]桃花林 良人吟 月晓风清\r\n[01:46.18]夜微凉 星空冷\r\n[01:50.43]花落谁看晨曦\r\n[01:54.22]这痴人曲 梦一世终会醒\r\n[02:00.65]月光下远去的背影\r\n[02:04.35]断琴弦心碎在手里\r\n[02:09.17]褪色的青衣\r\n[02:12.00]我依然还在等你\r\n[02:15.44]月光下远去的背影\r\n[02:19.13]繁星泪流干在心里\r\n[02:23.88]泼墨的痕迹\r\n[02:25.56]是今生的约定\r\n[02:30.09]Rap：\r\n[02:30.84]烟波里有草木清\r\n[02:32.62]好似我送你的青衣\r\n[02:34.60]桃花林中良人吟\r\n[02:36.36]谁人坐等 月晓风清\r\n[02:38.28]夜微凉星空孤冷\r\n[02:40.13]花落下是谁看晨曦\r\n[02:42.20]这痴人曲 梦的一世\r\n[02:43.96]也怕终究会醒\r\n[02:45.90]难演的剧情\r\n[02:46.96]谁肯相信将落幕的回忆呢\r\n[02:50.05]等雨落尽 笔墨尽\r\n[02:51.72]繁华也一旧如往昔\r\n[02:53.61]前世伏笔 等今生的你\r\n[02:55.54]相遇如场梦境吗\r\n[02:57.44]那缥缈如烟的年华\r\n[02:59.02]不如让它散落风里\r\n[03:00.13]月光下远去的背影\r\n[03:03.37]断琴弦心碎在手里\r\n[03:08.32]褪色的青衣\r\n[03:11.06]我依然还在等你\r\n[03:14.55]月光下远去的背影\r\n[03:18.19]繁星泪流干在心里\r\n[03:23.01]泼墨的痕迹\r\n[03:24.73]是今生的约定\r\n');
INSERT INTO `m_music` VALUES ('606', '侠风传', '灰老板', 'http://singerimg.kugou.com/uploadpic/softhead/400/20150805/20150805115818580862.jpg', 'D20577919BA7BA34BEE8090103D0C9B0', '小编每日精选：原创古风', '[00:03.23]作词：妖狐公子\r\n[00:04.49]作曲：WH宇恒\r\n[00:05.66]混音：WH宇恒\r\n[00:06.77]编曲：贺鑫、徐一\r\n[00:08.08]笛子：贺鑫\r\n[00:09.09]二胡：辰小弦\r\n[00:10.25]混音助力：栀枳\r\n[00:11.52]曲绘：草猹\r\n[00:12.68]PV：狗葱\r\n[00:13.65]海报：纳凉生\r\n[00:19.89]卡修：\r\n[00:21.31]我应踏鹤背下人间\r\n[00:24.14]叠山河 成方寸 负于肩\r\n[00:28.28]伦桑：\r\n[00:29.81]拦路横斜折去勾月\r\n[00:32.55]正好作 最明烛 来照雪\r\n[00:36.04]卡修：\r\n[00:36.75]歪坐懒马唱段闲\r\n[00:38.97]横生奇客拦前\r\n[00:41.05]买路须清高或宝剑\r\n[00:44.00]伦桑：\r\n[00:44.76]我恰有酒一杯半\r\n[00:47.38]一赠你半赠天\r\n[00:49.49]侠道长 正好知己缺\r\n[00:52.26]卡修、伦桑：\r\n[00:53.30]只管赴那纵险横煞 刀口斧下\r\n[00:57.46]非是一等奇绝不屑踏\r\n[01:01.42]只管碰碎金杯补漏瓦 见厌即杀\r\n[01:06.01]未曾狂妄岂敢称风华\r\n[01:25.68]宇恒：\r\n[01:27.95]市井酣卧朱门系马\r\n[01:30.83]天地大 侠心在 皆为家\r\n[01:34.27]灰老板：\r\n[01:36.18]鞘作杯沽酒是最雅\r\n[01:39.06]恨无友 却正好 遇个他\r\n[01:42.45]宇恒：\r\n[01:43.25]本是初蒙一书家\r\n[01:45.54]某日骤醒梦罢\r\n[01:47.58]遂卖尽书卷换酒马\r\n[01:50.86]灰老板：\r\n[01:51.67]相逢殷勤问饮否\r\n[01:53.95]醉里才好看花\r\n[01:56.21]待兴起 赴义何劳他\r\n[01:58.85]宇恒、灰老板：\r\n[01:59.86]我要立身天头云顶 万人仰煞\r\n[02:04.16]蔑弃无限风光于眼下\r\n[02:08.11]我要唤一念天地倒转 山平海塌\r\n[02:12.65]直缩万里成寸去天涯\r\n[02:40.34]合：\r\n[02:41.37]只管赴那纵险横煞 刀口斧下\r\n[02:45.77]非是一等奇绝不屑踏\r\n[02:49.77]只管碰碎金杯补漏瓦 见厌即杀\r\n[02:54.35]未曾狂妄岂敢称风华\r\n[02:58.39]我要立身天头云顶 万人仰煞\r\n[03:02.59]蔑弃无限风光于眼下\r\n[03:06.60]我要唤一念天地倒转 山平海塌\r\n[03:11.00]直缩万里成寸去天涯\r\n[03:15.20]浩荡川河作伴不归家\r\n[03:19.27]只留风骨月魄昭天下\r\n');
