INSERT INTO `m_music` VALUES ('609', '青衫薄', '萧忆情Alex', 'http://singerimg.kugou.com/uploadpic/softhead/400/20150812/20150812183732199005.jpg', 'D72AF040B9FCA51A039FE7C9833AF1F6', '小编每日精选：原创古风', '[00:00.37]KBShinya、哦漏、排骨教主、萧忆情Alex - 青衫薄\r\n[00:01.03]作词：冉语优\r\n[00:01.23]作曲：陈亦洺\r\n[00:01.44]编曲：Fsy小诺\r\n[00:01.69]二胡：辰小弦\r\n[00:12.68]合：\r\n[00:13.88]江城外送故人别归舟\r\n[00:17.47]驿桥下饮一杯新醅酒\r\n[00:21.05]君不见在远方槛外\r\n[00:23.81]千帆过尽 逝水东流\r\n[00:28.51]等夜泊寒山一声钟后\r\n[00:32.20]摆下笔墨写几句唱酬\r\n[00:35.88]不曾想故地重游\r\n[00:38.63]明日又到瓜洲\r\n[00:43.03]萧忆情：\r\n[00:44.76]三五岁开蒙不离左右\r\n[00:48.23]七八载苦读同窗挚友\r\n[00:51.97]当年从此地北去京华\r\n[00:55.33]与君同舟\r\n[00:58.36]排骨：\r\n[00:59.47]顺道访了黄鹤楼\r\n[01:03.16]白云悠悠\r\n[01:06.81]谁写出个盛唐风流\r\n[01:12.45]KB：\r\n[01:12.85]入京华试才名初交手\r\n[01:16.49]少年人还不识天地厚\r\n[01:20.18]都曾自负明心绣口\r\n[01:23.24]有多少人 老了白头\r\n[01:27.39]哦漏：\r\n[01:27.90]题名后我榜眼君榜首\r\n[01:31.31]襟边戴花听雨在歌楼\r\n[01:35.00]那时候相顾似乎\r\n[01:37.72]只一笑都忘忧\r\n[01:58.53]风尘仆仆又多少年后\r\n[02:02.01]赶赴任我江州君陇右\r\n[02:05.73]忽然被一曲商女琵琶\r\n[02:09.05]赋上离愁\r\n[02:12.29]KB：\r\n[02:13.32]座下谁人泪最多\r\n[02:16.95]湿了青袖\r\n[02:20.74]千丝万绪不能抬首\r\n[02:26.31]萧忆情：\r\n[02:26.96]马上偶然相逢不开口\r\n[02:30.38]折一枝理了还乱的柳\r\n[02:34.16]轻轻一拂也算握手\r\n[02:36.96]悲欢离合 乍起乍收\r\n[02:41.16]排骨：\r\n[02:41.62]后来在诗中却只是说\r\n[02:45.10]昨夜小楼又雨君知否\r\n[02:48.84]门外杏花纷纷落\r\n[02:51.60]似人间雪满头\r\n[02:55.90]合：\r\n[02:56.36]江城外送故人别归舟\r\n[02:59.94]驿桥下饮一杯新醅酒\r\n[03:03.56]君不见在远方槛外\r\n[03:06.37]千帆过尽 逝水东流\r\n[03:11.00]等夜泊寒山一声钟后\r\n[03:14.68]摆下笔墨写几句唱酬\r\n[03:18.38]不曾想故地重游\r\n[03:21.11]明日又到瓜洲\r\n');
INSERT INTO `m_music` VALUES ('610', '天光之外', '河图', 'http://singerimg.kugou.com/uploadpic/softhead/400/20151214/20151214120402408673.jpg', 'EB6FA147CC6F488EBCC830EF2B391328', '小编每日精选：原创古风', '[00:00.39]河图 - 天光之外\r\n[00:01.84]作词：冉语优\r\n[00:02.95]作曲：河图\r\n[00:03.81]和声：我是小吴太太\r\n[00:05.39]混音：我是小吴太太\r\n[00:07.01]编曲：河图\r\n[00:08.02]吉他：河图\r\n[00:08.99]贝斯：河图\r\n[00:10.12]二胡：河图\r\n[00:34.45]远隔着雾里楼台茫茫重山\r\n[00:38.06]记得那时节也烟波渺然\r\n[00:41.96]你说有故事一段\r\n[00:43.88]悠长得千回百转\r\n[00:45.91]终究未能对我说完\r\n[00:49.96]若从此不分谷雨抑或小寒\r\n[00:53.55]眺望着人间至灯火阑珊\r\n[00:57.40]危楼上拍遍栏杆\r\n[00:59.32]那一年你的故事\r\n[01:01.34]可否容我再次窥探\r\n[01:07.38]用毕生梦境\r\n[01:09.35]来交换某一眼流转顾盼\r\n[01:15.08]但凡足以求得\r\n[01:16.90]片刻都似圆满\r\n[01:18.82]余下一切神销魂断\r\n[01:22.10]云过巫山月上广寒\r\n[01:30.62]你说消散便为你消散\r\n[02:05.40]你衣上有胭脂色一地牡丹\r\n[02:08.93]相拥时出落得折皱一团\r\n[02:12.89]不免又心神迷乱\r\n[02:14.71]放手把衣尘轻掸\r\n[02:16.95]恍然一笑意兴阑珊\r\n[02:21.08]你眼底有秋水一泓最清湛\r\n[02:24.81]任谁凝神再望也望不穿\r\n[02:28.30]熨烫过五官百感\r\n[02:30.38]越饮就越生痴贪\r\n[02:32.40]刹那竟可同悲同欢\r\n[02:38.49]当风流始然\r\n[02:40.42]到最终也不免肝肠寸断\r\n[02:45.82]不如就在当时\r\n[02:47.79]最后转身回看\r\n[02:49.76]用尽所有深情款款\r\n[02:53.10]极尽虔诚\r\n[02:57.08]极尽温软\r\n[03:01.45]亦真亦假都宛如梦幻\r\n[03:09.17]这未竟故事\r\n[03:11.78]本应该左右与风月无关\r\n[03:16.69]却趁睡梦之中\r\n[03:18.87]天光次第黯淡\r\n[03:20.85]轻轻将谁眉尖舒展\r\n[03:24.24]跋涉不过迢迢银汉\r\n[03:32.56]却不经意打湿我衣衫\r\n');
