INSERT INTO `m_music` VALUES ('631', '千秋一顾', '邢万来(Ava)', 'http://singerimg.kugou.com/uploadpic/softhead/400/20180629/20180629143904754585.jpg', '47CF4412F3C22E1DA238318BFA6BF8A6', '小编每日精选：原创古风', '[00:00.13]邢万来 - 千秋一顾\r\n[00:01.36]作词：叶清眉\r\n[00:02.31]作曲：Willen\r\n[00:19.14]人说落日下彤霞千秋\r\n[00:22.88]举目红尘盈眸\r\n[00:25.56]好似浮生里牵一回手\r\n[00:29.15]就可行至大浪的尽头\r\n[00:33.87]人说碧水本无澜无忧\r\n[00:38.08]风起刹那吹皱\r\n[00:40.99]好似灵魂共鸣以后\r\n[00:44.49]鼎沸于怒海沧流\r\n[00:49.43]我曾于人潮随波逐流\r\n[00:52.98]幸遇你瀚海同舟\r\n[00:57.09]渡我过一帆风疏雨骤\r\n[01:00.57]将彼岸缤纷来收\r\n[01:04.72]我曾于高山自甘困囿\r\n[01:08.21]幸承你舍身相救\r\n[01:12.22]长路崎岖里江湖携手\r\n[01:15.68]从此无惧那天高地厚\r\n[01:40.73]人说春泥是深情落红\r\n[01:44.42]护得草木向荣\r\n[01:47.09]如同人世间情之所钟\r\n[01:50.67]一世护持生死皆与共\r\n[01:55.29]人说枝头生南国红豆\r\n[01:59.53]安入骰子玲珑\r\n[02:02.33]如同相思侵入血肉\r\n[02:06.17]不知其所起所终\r\n[02:10.79]若命数赠你人间一走\r\n[02:14.52]却是我得天独厚\r\n[02:18.50]岁月偏宠才知遇邂逅\r\n[02:22.00]相守到暮雪白头\r\n[02:25.89]若他人判我情深不寿\r\n[02:29.61]当是你难辞其咎\r\n[02:33.49]浩荡余生付天真诗酒\r\n[02:37.18]与你共饮便不醉不休\r\n[02:44.85]春树暮云 玉露金风\r\n[02:48.53]八方万物何处不情浓\r\n[02:52.37]众生有所钟\r\n[02:54.65]一如爱意葱茏\r\n[03:00.06]若命数赠你人间一走\r\n[03:03.72]却是我得天独厚\r\n[03:07.72]岁月偏宠才知遇邂逅\r\n[03:11.34]相守到暮雪白头\r\n[03:15.12]若他人判我情深不寿\r\n[03:18.88]当是你难辞其咎\r\n[03:22.88]浩荡余生付天真诗酒\r\n[03:26.22]与你共饮便不醉不休\r\n');
